import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { todoInputActions } from "../store";

const TodoListInput = () => {
  const [todoText, setTodoText] = useState("");
  const dispatch = useDispatch();
  const inputText = useSelector((state) => state.todoInput.text);

  const handleChange = (e) => {
    setTodoText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    dispatch(todoInputActions.add(todoText));
    console.log(inputText);
  };
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label for="todoInput">할 일을 입력하세요</label>
        <input id="todoInput" onChange={handleChange}></input>
        <button>입력</button>
      </form>
    </div>
  );
};

export default TodoListInput;
