import { Typography, Button } from "@mui/material";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { doneTodoListActions, undoneTodoListActions } from "../store";
import styled from "styled-components";

const StyledUl = styled.ul`
  list-style-type: none;
  padding: 1rem;
  margin: 0;
`;

const ListChild = styled.div`
  display: flex;
  border-bottom: 5px solid grey;
`;

const TodoListContainer = styled.div`
  display: flex;
`;

const TodoList = () => {
  const undoTodoList = useSelector((state) => state.undoneTodoList.list);

  const dispatch = useDispatch();

  const handleComplete = (key) => {
    //완료된 todo를 찾아
    const completedTodo = undoTodoList.filter((it) => it.key === key);

    dispatch(doneTodoListActions.add(completedTodo[0]));
    dispatch(undoneTodoListActions.remove(key));
  };

  useEffect(() => {
    console.log(undoTodoList);
  }, [undoTodoList]);

  return (
    <div className="undoTodoList">
      <Typography variant="h6">UnDoneTodoList</Typography>

      <StyledUl>
        {undoTodoList.map((it) => {
          if (it.key > 0) {
            return (
              <TodoListContainer>
                <ListChild>
                  <li>{it.content}</li>
                  <Button
                    variant="outlined"
                    key={it.key}
                    onClick={() => {
                      handleComplete(it.key);
                    }}
                  >
                    완료!
                  </Button>
                </ListChild>
              </TodoListContainer>
            );
          }
        })}
      </StyledUl>
    </div>
  );
};

export default TodoList;
