import { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { todoInputActions, undoneTodoListActions } from "../store";

const TodoListInput = () => {
  const [todoInput, setTodoInput] = useState("");
  const [todoObject, setTodoText] = useState({
    content: "",
    key: 0,
  });
  const id = useRef(0);
  const dispatch = useDispatch();
  const inputText = useSelector((state) => state.todoInput.text);
  const undoTodoList = useSelector((state) => state.undoneTodoList.list);

  const handleChange = (e) => {
    setTodoInput(e.target.value);
    setTodoText({
      content: e.target.value,
      key: id++,
    });
    // console.log(e);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    dispatch(todoInputActions.add(todoObject));
    dispatch(undoneTodoListActions.add(todoObject));

    setTodoText({
      content: "",
      key: 0,
    });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label for="todoInput">할 일을 입력하세요</label>
        <input
          id="todoInput"
          onChange={handleChange}
          value={todoObject.content}
        ></input>
        <button>입력</button>
      </form>
    </div>
  );
};

export default TodoListInput;
